const EmployeeList = ({ categoryList }) => {
  return (
    <table border="1">
      <tr>
        <th>ID</th>
        <th>社員名</th>
        <th>営業担当</th>
        <th>投稿数</th>
        <th>最終投稿情報</th>
        <th>共有情報ボタン</th>
      </tr>
      {categoryList.map((val, index) => (
        <>
          <tr key={index}>
            <td>{val.id}</td>
            <td>
              {val.name_kanji}
              <br />
              {val.name_kana}
