import React, { useState, useEffect } from "react";
import { useParams } from 'react-router-dom';
import axiosInstance from "../api/axiosInstance";

const FeedbackForm = (addFeedback) => {
  const [employee, setEmployee] = useState([]);
  const { id } = useParams();
  const [select, setSelect] = useState("keep");
  const [feedback, setFeedback] = useState();
  const [date, setDate] = useState([]);
  const [time, setTime] = useState([]);

  useEffect(() => {
    //axiosInstance.jsを通じてAPIリクエストを送る
    axiosInstance
      .get(`/getFeedback/${id}`)
      .then((response) => setEmployee(response.data))
      .catch((error) => console.error('フィードバック情報の取得に失敗しました：', error));
  }, [id]);

  if (employee.length === 0) {
    return <div>情報読み込み中...</div>;
  }

  useEffect(() => {
    setInterval(() => {
      let d = new Date();
      let year = d.getFullYear();
      let month = d.getMonth() + 1;
      let day = d.getDate();

      setDate(year + "年" + month + "月" + day + "日");

      let hour = d.getHours().toString().padStart(2, "0");
      let minute = d.getMinutes().toString().padStart(2, "0");
      setTime(hour + ":" + minute);
    }, 1000);
  });

  const datetime = { date } + { time };

  return (
    <>
      <hr />
      <label>テスト日付</label>
      <hr/>
      <div>
        <p></p>
      </div>
      <form>
        <input
          type="radio"
          name="category"
          onChange={(e) => setSelect(e.target.value)}
          value="keep"
          checked={select === "keep"}
        />
        ポジティブ
        <input
          type="radio"
          name="category"
          onChange={(e) => setSelect(e.target.value)}
          value="problem"
        />
        ネガティブ
        <input
          type="radio"
          name="category"
          onChange={(e) => setSelect(e.target.value)}
          value="try"
        />
        その他
        <br />
        <textarea
          value={feedback}
          onChange={(e) => setFeedback(e.target.value)}
        ></textarea>
        <input
          type="submit"
          onClick={() => addFeedback(select, feedback, datetime)}
        />
      </form>
    </>
  );
};

export default FeedbackForm;
