// import React from "react";
import React, { useState, useEffect, useRef } from "react";
// import axiosInstance from "../api/axiosInstance";

const FeedbackForm = () => {
  const [select, setSelect] = useState();
  const [feedback, setFeedback] = useState();
  const [date, setDate] = useState([]);
  const [time, setTime] = useState([]);

  useEffect(() => {
    setInterval(() => {
      let d = new Date();
      let year = d.getFullYear();
      let month = d.getMonth() + 1;
      let day = d.getDate();

      setDate(
        year + "年" + month + "月" + day + "日" + "[" + dayname[dayofweek] + "]"
      );

      let hour = d.getHours().toString().padStart(2, "0");
      let minute = d.getMinutes().toString().padStart(2, "0");
      setTime(hour + ":" + minute);
    });
  }, []);

  const ref = useRef(0);

  const selectChange = (e) => {
    setSelect(e.target.value);
    console.log(select);
  };

  const handleChange = (e) => {
    setFeedback(e.target.value);
  };

  const handleClick = () => {
    ref.current = [{ "feedback_type": { select }, "content": { feedback }, "created_at":{date}+{time} }];
  };

  return (
    <form>
      <div onChange={selectChange}>
        <input type="radio" name="category" value="keep" />
        ポジティブ
        <input type="radio" name="category" value="problem" />
        ネガティブ
        <input type="radio" name="category" value="try" />
        その他
        <br />
      </div>
      <textarea onChange={handleChange}>あいうえお</textarea>
      <input type="submit" onClick={handleClick} />
    </form>
  );
};

export default FeedbackForm;
