import React, { useState, useEffect } from "react";
import axiosInstance from "../api/axiosInstance";

const EmployeeDetail = () => {
  const [employee, setEmployee] = useState([]);

  useEffect(() => {
    //axiosInstance.jsを通じてAPIリクエストを送る
    axiosInstance
      .get('/detail')
      .then((response) => setEmployee(response.data))
      .catch((error) => console.error('情報の取得に失敗しました：', error));
  }, []);

  if (employee.length === 0) {
    return <p>読み込み中...</p>; // データが取得できていない場合の表示
  }
  const display = employee[0];

  return (
    <table border="1">
      <tr>
        <th>社員名</th>
        <td>{display.name_kanji}</td>
      </tr>
      <tr>
        <th>部署</th>
        <td>{display.department}</td>
        <th>役職</th>
        <td>{display.position}</td>
      </tr>
      <tr>
        <th>営業担当</th>
        <td>{display.sales_kanji}</td>
      </tr>
      <tr>
        <th>直属上司</th>
        <td>　　　　　</td>
        <th>査定実施者</th>
        <td>　　　　　</td>
      </tr>
    </table>
  );
};

export default EmployeeDetail;
