import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import employeeService from "../services/employeeService";

const FeedbackForm = (addFeedback) => {
  const [employee, setEmployee] = useState([]);
  const { id } = useParams();
  const [select, setSelect] = useState("keep");
  const [feedback, setFeedback] = useState();
  const [date, setDate] = useState([]);
  const [time, setTime] = useState([]);

  useEffect(() => {
    // employeeService.jsを通じてAPIリクエストを送る
    employeeService.getFeedback(id)
      .then((response) => setEmployee(response.data))
      .catch((error) => console.error("フィードバック情報の取得に失敗しました：", error));
  }, [id]);

  useEffect(() => {
    setInterval(() => {
      let d = new Date();
      let year = d.getFullYear();
      let month = d.getMonth() + 1;
      let day = d.getDate();

      setDate(year + "年" + month + "月" + day + "日");

      let hour = d.getHours().toString().padStart(2, "0");
      let minute = d.getMinutes().toString().padStart(2, "0");
      setTime(hour + ":" + minute);
    }, 1000);
  });

  
  const datetime = { date } + { time };

  employeeService.addFeedback(id, select, feedback, datetime)
  .then(() => {
    alert("フィードバックが送信されました！");
    setFeedback(""); // フィードバックを送信した後にフィールドをリセット
  })
  .catch((error) => {
    console.error("フィードバックの送信に失敗しました：", error);
    alert("フィードバック送信に失敗しました。");
  });

  return (
    <>
      <div>
        <hr />
        <label>テスト日付</label>
        <hr />
        <div>
          <p>{employee.created_at}テスト：作成日</p>
          <hr />
          <p id={employee.feedback_type}>{employee.content}テスト：内容</p>
        </div>
      </div>
      <hr />
      <form onSubmit={handleSubmit}>
        <input
          type="radio"
          name="category"
          onChange={(e) => setSelect(e.target.value)}
          value="keep"
          checked={select === "keep"}
        />
        ポジティブ
        <input
          type="radio"
          name="category"
          onChange={(e) => setSelect(e.target.value)}
          value="problem"
        />
        ネガティブ
        <input
          type="radio"
          name="category"
          onChange={(e) => setSelect(e.target.value)}
          value="try"
        />
        その他
        <br />
        <textarea
          value={feedback}
          onChange={(e) => setFeedback(e.target.value)}
        ></textarea>
        <button onClick={() => addFeedback(select, feedback, datetime)}>
          投稿
        </button>
      </form>
    </>
  );
};

export default FeedbackForm;
